    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int index = scanner.nextInt();
        int n = scanner.nextInt();
        
        String result = getDigits(index, n);
        System.out.println(result);
        
        scanner.close();
    }
    
    private static String getDigits(int startIndex, int length) {
        StringBuilder sb = new StringBuilder();
        String[] encodedParts = {ENCODED_PI_PART1, ENCODED_PI_PART2, ENCODED_PI_PART3, ENCODED_PI_PART4, ENCODED_PI_PART5,ENCODED_PI_PART6, ENCODED_PI_PART7};
        System.err.println(ENCODED_PI_PART1.length() + " " + ENCODED_PI_PART2.length() + " " + ENCODED_PI_PART3.length() + " " + startIndex);
        int currentIndex = 0;
        int endIndex = startIndex + length;
        
        for (String encodedPart : encodedParts) {
            for (char c : encodedPart.toCharArray()) {
                int value = c - BASE_CHAR;
                String fourDigits = String.format("%04d", value);
                
                // Process each of the 4 digits
                for (int i = 0; i < 4; i++) {
                    if (currentIndex >= startIndex && currentIndex < endIndex) {
                        sb.append(fourDigits.charAt(i));
                    }
                    currentIndex++;
                    
                    // Early exit if we've collected enough digits
                    if (sb.length() == length) {
                        return sb.toString();
                    }
                }
            }
        }
        
        return sb.toString();
    }
